import React, { useState, useEffect } from 'react'
import { fetchData } from '../store/actions/fetchData'
import { useDispatch, useSelector } from 'react-redux'

function QuestionCard() {

    const dispatch = useDispatch()
    const state = useSelector((state) => { return state })
    if (state[1]) {
        console.log(state.questions)
    }

    useEffect(() => {
        fetchData(dispatch)

    }, [])

    let [count, setCount] = useState(1)
    let [answer, setAnswer] = useState('')
    let [color, setColor] = useState('rgb(216, 216, 131)')



    function ctaButton(e) {
        setColor('rgb(216, 216, 131)')
        e.target.style.backgroundColor = 'yellow'
    }

    return (
        <div className="card">

            {state.loading ? 'loading...' : <h2>{state.questions[count].question}</h2>}
            {state.loading ? null : state.questions[count].incorrect_answers.map((item) => { return <button onClick={ctaButton} style={{ backgroundColor: { color } }} >{item}</button> })}
            {state.loading ? null : <button style={{ color: 'red' }} onClick={ctaButton}>{state.questions[count].correct_answer}</button>}
        </div>
    )
}

export default QuestionCard
