import { UPLOAD_DATA } from "../actionType/actionType"

let initialState = {
    loading: true,
    questions: [],
}


export const questionReducer = (state = initialState, action) => {
    switch (action.type) {

        case UPLOAD_DATA:
            // console.log(action.payLoad)
            return {
                ...state,
                questions: action.payLoad
            }
        default:
            return state
    }
}
