import React, { useState, useEffect } from 'react'
import { fetchData } from '../store/actions/fetchData'
import { useDispatch, useSelector } from 'react-redux'

function QuestionCard() {

    const dispatch = useDispatch()
    const state = useSelector((state) => { return state })
    if (state[1]) {
        console.log(state.questions)
    }

    useEffect(() => {
        fetchData(dispatch)

    }, [])

    let [count, setCount] = useState(1)


    return (
        <div className="card">
            <h2>Which of the following author is not born in useHistory ?</h2>
            {state.loading ? 'loading...' : state.questions[count].question}
            {state.loading ? null : state.questions[count].incorrect_answers.map((item) => { return <button>{item}</button> })}
        </div>
    )
}

export default QuestionCard
