import { UPLOAD_DATA } from "../actionType/actionType"


export const fetchData = async (dispatch, state) => {

    try {
        console.log(dispatch)
        let res = await fetch('https://opentdb.com/api.php?amount=10&category=10&difficulty=medium&type=multiple')
        let data = await res.json();
        let answers = await state.questions.map((item) => { return item.correct_answer })
        console.log(data.results);
        dispatch({
            type: UPLOAD_DATA,
            payLoad: data.results,
            answers
        })
    } catch (err) {
        console.error(err)
    }



}
